import { useForm } from 'react-hook-form';

async function addFileHandler(data) {
  console.log(data);
  const fileText = await data.file.item(0).text();
  console.log(fileText);

  //const writeFileP = require("write-file-p");
  // Write a text file
  //writeFileP(`${__dirname}/foo/bar/output.txt`, fileText, (err, data) => {
  //    console.log(err || data);
  //});
}

function Account(props) {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  let files = [
    {"id": "1", "filename": "asd", "is_deleted": false},
    {"id": "2", "filename": "vcb", "is_deleted": true},
    {"id": "3", "filename": "zerg", "is_deleted": false},
  ];

  return (
    <table style={{'tableLayout': 'fixed', 'width': '100%', 'height': '100%'}}>
      <tbody>
        <tr style={{"height": "66%"}}>
          <td style={{"width": "30%"}}>
            <div><p>Username: {localStorage.getItem("username")}</p></div>
            <div><p>Email: ?????</p></div>
            <div><p>Full name: ?????</p></div>
            <div><p>Date of birth: ?????</p></div>
          </td>
          <td rowSpan="2" style={{"width": "60%"}}>
            {files.map(elem => (
              <div key={elem.id}>
                <p>{elem.filename}</p>
                <button>Download</button>
                <button>Delete</button>
              </div>
            ))}
          </td>
        </tr>
        <tr style={{"height": "33%"}}>
          <td style={{"width": "30%"}}>
            <form onSubmit={handleSubmit(addFileHandler)}>
              <div>
                  <label>Filename</label>
                  <input {...register('filename', { required: true })} />
                  {errors.filename && <p>Filename is required.</p>}
              </div>
              <div>
                  <label>File</label>
                  <input type='file' {...register('file', { required: true })} />
                  {errors.file && <p>File is required.</p>}
              </div>
              <button>Upload file</button>
            </form>
          </td>
        </tr>
      </tbody>
    </table>
  );
}

export default Account;
